var footer=document.querySelector("div#jiesuanbar");const maxY=document.documentElement.scrollHeight-document.documentElement.clientHeight;function func2(){const b=window.scrollY;const a=maxY-25;if(b>=a){footer.style.marginBottom="80px";footer.style.transition="margin-bottom 0.25s"}else{footer.style.margin="0"}}function func1(){window.addEventListener("scroll",func2)}window.onload=func1;function countchecked(){let count=0;$(".formdata").each(function(a,c){const b=$(c).eq(0).find("input").is(":checked");if(b){count++}});if(count==0){$("#confbutt").css("background","#aaa")}else{$("#confbutt").css("background","#333")}}countchecked();$(".selectallinput").click(function(){countchecked()});